// ============================================
// LOCK SLOT - Prepare event data
// ============================================
const slot = $json.chosen_slot;
const patient = $json.sender_name || 'Paciente';
const phone = $json.contact_phone || 'No disponible';

// Extract service type from original message (basic extraction)
const original_msg = ($json.message_text || '').toLowerCase();
let service = 'Consulta general';

if (original_msg.includes('limpieza')) {
    service = 'Limpieza dental';
} else if (original_msg.includes('blanqueamiento')) {
    service = 'Blanqueamiento dental';
} else if (original_msg.includes('ortodoncia')) {
    service = 'Ortodoncia';
} else if (original_msg.includes('extracción') || original_msg.includes('extraccion')) {
    service = 'Extracción';
} else if (original_msg.includes('endodoncia') || original_msg.includes('conducto')) {
    service = 'Endodoncia';
} else if (original_msg.includes('implante')) {
    service = 'Implante dental';
}

return [{
    json: {
        ...$json,
        event_summary: `${service} - ${patient}`,
        event_description: `Paciente: ${patient}\nTeléfono: ${phone}\nServicio: ${service}\n\nAgendado automáticamente por SofIA Bot`,
        event_start: slot.start_iso,
        event_end: slot.end_iso,
        event_location: 'Clínica Dental SofIA Dent',
        service_type: service
    }
}];

// ============================================
// VALIDAR INPUT + DETECTAR CONTACTO NUEVO
// ============================================
const payload = $input.item.json.body || $input.item.json;

// Campos del evento
const event = payload.event;
const content = payload.content || '';
const conversation_id = payload.conversation?.id;
const inbox_id = payload.conversation?.inbox_id;
const clinic_id = payload.conversation?.custom_attributes?.clinic_id;
const patient_id = payload.conversation?.custom_attributes?.patient_id;
const contact_phone = payload.conversation?.contact_inbox?.source_id;
const contact_id = payload.sender?.id;
const channel_type = payload.conversation?.contact_inbox?.inbox?.channel_type;
const account_id = payload.account?.id || 2;
const message_type = payload.message_type;
const created_at = payload.created_at;

// Obtener contador de interacciones bot
const bot_count = payload.conversation?.custom_attributes?.bot_interaction_count || 0;
const last_bot_message = payload.conversation?.custom_attributes?.last_bot_message_at;
const contact_inboxes = payload.conversation?.contact_inbox;
const has_contact_inbox = !!contact_inboxes;

return [{
  json: {
    message_text: (content || '').trim(),
    conversation_id: conversation_id,
    inbox_id: inbox_id,
    inbox_id_api: 2,
    inbox_id_db: 3,
    clinic_id: clinic_id || 'default',
    patient_id: patient_id,
    contact_phone: contact_phone,
    contact_id: contact_id,
    account_id: account_id,
    message_type: message_type,
    message_timestamp: created_at,
    bot_interaction_count: bot_count,
    last_bot_message_at: last_bot_message,
    sender_name: payload.sender?.name || 'Paciente',
    sender_email: payload.sender?.email,
    conversation_status: payload.conversation?.status,
    has_contact_inbox: has_contact_inbox,
    channel_type: channel_type || 'Channel::WebWidget',
    raw_payload: payload
  }
}];

// ============================================
// WHATSAPP SAFE RULES - FASE 1
// ============================================
const bot_count = $json.bot_interaction_count || 0;
const message_lower = ($json.message_text || '').toLowerCase();

// Regla 1: MÃƒÆ’Ã†â€™Ãƒâ€šÃ‚Â¡ximo 1 interacciÃƒÆ’Ã†â€™Ãƒâ€šÃ‚Â³n automÃƒÆ’Ã†â€™Ãƒâ€šÃ‚Â¡tica en Fase 1
if (bot_count >= 1) {
  return [{
    json: {
      ...$json,
      should_escalate: true,
      escalation_reason: 'MAX_INTERACTIONS_PHASE1',
      debug_rule: 'RULE_1_MAX_INTERACTIONS',
      debug_bot_count: bot_count,
      escalation_message: 'Te conecto con un agente de inmediato.'
    }
  }];
}

// Regla 2: Mensaje > 24h
const message_age_hours = (Date.now() - ($json.message_timestamp * 1000)) / (1000 * 60 * 60);
if (message_age_hours > 24) {
  return [{
    json: {
      ...$json,
      should_escalate: true,
      escalation_reason: 'MESSAGE_TOO_OLD',
      debug_rule: 'RULE_2_MESSAGE_AGE',
      debug_age_hours: message_age_hours,
      escalation_message: 'Te conecto con un agente.'
    }
  }];
}

// Regla 3: Horario comercial (Lima, Peru)
const now = new Date();
const lima_hour = parseInt(now.toLocaleString('en-US', { 
  timeZone: 'America/Lima', 
  hour: 'numeric', 
  hour12: false 
}));

if (lima_hour < 8 || lima_hour >= 22) {
  return [{
    json: {
      ...$json,
      should_escalate: true,
      escalation_reason: 'OUTSIDE_BUSINESS_HOURS',
      debug_rule: 'RULE_3_HOURS',
      debug_lima_hour: lima_hour,
      debug_now: now.toISOString(),
      escalation_message: 'Gracias por escribirnos. Te responderemos en horario de atenciÃƒÆ’Ã†â€™Ãƒâ€šÃ‚Â³n (8am - 10pm).'
    }
  }];
}

// Regla 4: Emergencias
const emergency_keywords = [
  'emergencia', 'urgencia', 'dolor fuerte', 'sangra', 'mucho dolor',
  'me caÃƒÆ’Ã†â€™Ãƒâ€šÃ‚Â­', 'golpe', 'accidente', 'no puedo', 'ayuda urgente',
  'hinchazÃƒÆ’Ã†â€™Ãƒâ€šÃ‚Â³n', 'infecciÃƒÆ’Ã†â€™Ãƒâ€šÃ‚Â³n', 'fiebre'
];
const is_emergency = emergency_keywords.some(kw => message_lower.includes(kw));

if (is_emergency) {
  return [{
    json: {
      ...$json,
      should_escalate: true,
      escalation_reason: 'EMERGENCY_DETECTED',
      debug_rule: 'RULE_4_EMERGENCY',
      escalation_message: 'Detecto que podrÃƒÆ’Ã†â€™Ãƒâ€šÃ‚Â­as tener una urgencia. Te conecto de inmediato con nuestro equipo.',
      priority: 'urgent'
    }
  }];
}

// Regla 5: Opt-out
const opt_out_keywords = ['detente', 'ya no', 'basta', 'stop', 'cancelar bot', 'agente', 'humano', 'persona'];
const wants_opt_out = opt_out_keywords.some(kw => message_lower.includes(kw));

if (wants_opt_out) {
  return [{
    json: {
      ...$json,
      should_escalate: true,
      escalation_reason: 'USER_OPT_OUT',
      debug_rule: 'RULE_5_OPT_OUT',
      escalation_message: 'Entendido. Te conecto con un agente humano.'
    }
  }];
}

// Todo OK - continuar al clasificador
return [{
  json: {
    ...$json,
    should_escalate: false,
    whatsapp_safe: true,
    debug_rule: 'NO_ESCALATION_ALL_OK',
    debug_bot_count: bot_count,
    debug_lima_hour: lima_hour,
    checked_at: new Date().toISOString()
  }
}];

// ============================================
// NORMALIZAR OUTPUT DEL CLASIFICADOR
// Check if Pre-Clasificador already classified
// ============================================

// If Pre-Clasificador already classified, use that
if ($json.skip_ai === true && $json.intent) {
  return [{
    json: {
      ...$json,
      classified_at: new Date().toISOString(),
      phase: 'PHASE_1_WITH_PRE_CLASSIFIER'
    }
  }];
}

// Otherwise, parse AI Clasificador output
let intent_data = $json.output || $json;

if (typeof intent_data === 'string') {
  try {
    intent_data = JSON.parse(intent_data);
  } catch (e) {
    const text = intent_data.toLowerCase();
    if (text.includes('create_event')) {
      intent_data = { intent: 'CREATE_EVENT', confidence: 'low' };
    } else if (text.includes('info')) {
      intent_data = { intent: 'INFO', confidence: 'low' };
    } else if (text.includes('payment')) {
      intent_data = { intent: 'PAYMENT', confidence: 'low' };
    } else {
      intent_data = { intent: 'HUMAN', confidence: 'low' };
    }
  }
}

let intent = (intent_data.intent || 'HUMAN').trim().toUpperCase();
let confidence = (intent_data.confidence || 'low').toLowerCase();

const valid_intents = ['CREATE_EVENT', 'INFO', 'PAYMENT', 'HUMAN'];
if (!valid_intents.includes(intent)) {
  intent = 'HUMAN';
  confidence = 'low';
}

return [{
  json: {
    ...$json,
    intent: intent,
    confidence: confidence,
    classified_at: new Date().toISOString(),
    phase: 'PHASE_1_WITH_AI_CLASSIFIER'
  }
}];

// Preservar TODOS los datos del input
return [{
  json: {
    ...$json,
    escalation_message_final: $json.escalation_message || 'Te conecto con un agente de nuestro equipo.',
    escalation_reason_final: $json.escalation_reason || 'PHASE_1_' + $json.intent,
    internal_note: 'SofIA (Fase 1) - Intent: ' + $json.intent,
    is_urgent: $json.priority === 'urgent'
  }
}];

// ============================================
// KNOWLEDGE BASE - FASE 2
// Base de conocimiento estática de la clínica
// ============================================

const KNOWLEDGE_BASE = {
