// ============================================
// VALIDAR INPUT + DETECTAR CONTACTO NUEVO
// ============================================
const payload = $input.item.json.body || $input.item.json;

// Campos del evento
const event = payload.event;
const content = payload.content || '';
const conversation_id = payload.conversation?.id;
const inbox_id = payload.conversation?.inbox_id;
const clinic_id = payload.conversation?.custom_attributes?.clinic_id;
const patient_id = payload.conversation?.custom_attributes?.patient_id;
const contact_phone = payload.conversation?.contact_inbox?.source_id;
const contact_id = payload.sender?.id;
const channel_type = payload.conversation?.contact_inbox?.inbox?.channel_type;
const account_id = payload.account?.id || 2;
const message_type = payload.message_type;
const created_at = payload.created_at;

// Obtener contador de interacciones bot
const bot_count = payload.conversation?.custom_attributes?.bot_interaction_count || 0;
const last_bot_message = payload.conversation?.custom_attributes?.last_bot_message_at;
const contact_inboxes = payload.conversation?.contact_inbox;
const has_contact_inbox = !!contact_inboxes;

return [{
  json: {
    message_text: (content || '').trim(),
    conversation_id: conversation_id,
    inbox_id: inbox_id,
    inbox_id_api: 2,
    inbox_id_db: 3,
    clinic_id: clinic_id || 'default',
    patient_id: patient_id,
    contact_phone: contact_phone,
    contact_id: contact_id,
    account_id: account_id,
    message_type: message_type,
    message_timestamp: created_at,
    bot_interaction_count: bot_count,
    last_bot_message_at: last_bot_message,
    sender_name: payload.sender?.name || 'Paciente',
    sender_email: payload.sender?.email,
    conversation_status: payload.conversation?.status,
    has_contact_inbox: has_contact_inbox,
    channel_type: channel_type || 'Channel::WebWidget',
    raw_payload: payload
  }
}];

// ============================================
// WHATSAPP SAFE RULES - FASE 1
// ============================================
const bot_count = $json.bot_interaction_count || 0;
const message_lower = ($json.message_text || '').toLowerCase();

// Regla 1: MÃƒÆ’Ã†â€™Ãƒâ€šÃ‚Â¡ximo 1 interacciÃƒÆ’Ã†â€™Ãƒâ€šÃ‚Â³n automÃƒÆ’Ã†â€™Ãƒâ€šÃ‚Â¡tica en Fase 1
if (bot_count >= 1) {
  return [{
    json: {
      ...$json,
      should_escalate: true,
      escalation_reason: 'MAX_INTERACTIONS_PHASE1',
      debug_rule: 'RULE_1_MAX_INTERACTIONS',
      debug_bot_count: bot_count,
      escalation_message: 'Te conecto con un agente de inmediato.'
    }
  }];
}

// Regla 2: Mensaje > 24h
const message_age_hours = (Date.now() - ($json.message_timestamp * 1000)) / (1000 * 60 * 60);
if (message_age_hours > 24) {
  return [{
    json: {
      ...$json,
      should_escalate: true,
      escalation_reason: 'MESSAGE_TOO_OLD',
      debug_rule: 'RULE_2_MESSAGE_AGE',
      debug_age_hours: message_age_hours,
      escalation_message: 'Te conecto con un agente.'
    }
  }];
}

// Regla 3: Horario comercial (Lima, Peru)
const now = new Date();
const lima_hour = parseInt(now.toLocaleString('en-US', { 
  timeZone: 'America/Lima', 
  hour: 'numeric', 
  hour12: false 
}));

if (lima_hour < 8 || lima_hour >= 22) {
  return [{
    json: {
      ...$json,
      should_escalate: true,
      escalation_reason: 'OUTSIDE_BUSINESS_HOURS',
      debug_rule: 'RULE_3_HOURS',
      debug_lima_hour: lima_hour,
      debug_now: now.toISOString(),
      escalation_message: 'Gracias por escribirnos. Te responderemos en horario de atenciÃƒÆ’Ã†â€™Ãƒâ€šÃ‚Â³n (8am - 10pm).'
    }
  }];
}

// Regla 4: Emergencias
const emergency_keywords = [
  'emergencia', 'urgencia', 'dolor fuerte', 'sangra', 'mucho dolor',
  'me caÃƒÆ’Ã†â€™Ãƒâ€šÃ‚Â­', 'golpe', 'accidente', 'no puedo', 'ayuda urgente',
  'hinchazÃƒÆ’Ã†â€™Ãƒâ€šÃ‚Â³n', 'infecciÃƒÆ’Ã†â€™Ãƒâ€šÃ‚Â³n', 'fiebre'
];
const is_emergency = emergency_keywords.some(kw => message_lower.includes(kw));

if (is_emergency) {
  return [{
    json: {
      ...$json,
      should_escalate: true,
      escalation_reason: 'EMERGENCY_DETECTED',
      debug_rule: 'RULE_4_EMERGENCY',
      escalation_message: 'Detecto que podrÃƒÆ’Ã†â€™Ãƒâ€šÃ‚Â­as tener una urgencia. Te conecto de inmediato con nuestro equipo.',
      priority: 'urgent'
    }
  }];
}

// Regla 5: Opt-out
const opt_out_keywords = ['detente', 'ya no', 'basta', 'stop', 'cancelar bot', 'agente', 'humano', 'persona'];
const wants_opt_out = opt_out_keywords.some(kw => message_lower.includes(kw));

if (wants_opt_out) {
  return [{
    json: {
      ...$json,
      should_escalate: true,
      escalation_reason: 'USER_OPT_OUT',
      debug_rule: 'RULE_5_OPT_OUT',
      escalation_message: 'Entendido. Te conecto con un agente humano.'
    }
  }];
}

// Todo OK - continuar al clasificador
return [{
  json: {
    ...$json,
    should_escalate: false,
    whatsapp_safe: true,
    debug_rule: 'NO_ESCALATION_ALL_OK',
    debug_bot_count: bot_count,
    debug_lima_hour: lima_hour,
    checked_at: new Date().toISOString()
  }
}];

// ============================================
// NORMALIZAR OUTPUT DEL CLASIFICADOR
// ============================================
let intent_data = $json.output || $json;

if (typeof intent_data === 'string') {
  try {
    intent_data = JSON.parse(intent_data);
  } catch (e) {
    const text = intent_data.toLowerCase();
    if (text.includes('create_event')) {
      intent_data = { intent: 'CREATE_EVENT', confidence: 'low' };
    } else if (text.includes('info')) {
      intent_data = { intent: 'INFO', confidence: 'low' };
    } else if (text.includes('payment')) {
      intent_data = { intent: 'PAYMENT', confidence: 'low' };
    } else {
      intent_data = { intent: 'HUMAN', confidence: 'low' };
    }
  }
}

let intent = (intent_data.intent || 'HUMAN').trim().toUpperCase();
let confidence = (intent_data.confidence || 'low').toLowerCase();

const valid_intents = ['CREATE_EVENT', 'INFO', 'PAYMENT', 'HUMAN'];
if (!valid_intents.includes(intent)) {
  intent = 'HUMAN';
  confidence = 'low';
}

return [{
  json: {
    ...$json,
    intent: intent,
    confidence: confidence,
    classified_at: new Date().toISOString(),
    phase: 'PHASE_1_TESTING',
    action: 'ESCALATE_TO_HUMAN'
  }
}];

// Preservar TODOS los datos del input
return [{
  json: {
    ...$json,
    escalation_message_final: $json.escalation_message || 'Te conecto con un agente de nuestro equipo.',
    escalation_reason_final: $json.escalation_reason || 'PHASE_1_' + $json.intent,
    internal_note: 'SofIA (Fase 1) - Intent: ' + $json.intent,
    is_urgent: $json.priority === 'urgent'
  }
}];

// ============================================
// KNOWLEDGE BASE - FASE 2
// Base de conocimiento estática de la clínica
// ============================================

const KNOWLEDGE_BASE = {
  "clinic_info": {
    "name": "Clínica Dental SofIA Dent (Test)",
    "address": "Av. Principal 123, San Isidro, Lima, Perú",
    "phone": "+51 905 858 566",
    "email": "info@redsolucionesti.com",
    "website": "https://sofia-test.redsolucionesti.com"
  },
  "hours": {
    "weekdays": "Lunes a Viernes: 9:00 AM - 7:00 PM",
    "saturday": "Sábados: 9:00 AM - 2:00 PM",
    "sunday": "Domingos: Cerrado"
  },
  "services": [
    {
      "name": "Limpieza dental",
      "price_range": "S/ 80 - S/ 150",
      "duration": "30-45 minutos",
      "description": "Limpieza profesional con ultrasonido"
    },
    {
      "name": "Blanqueamiento dental",
      "price_range": "S/ 300 - S/ 500",
      "duration": "60 minutos",
      "description": "Blanqueamiento LED profesional"
    },
    {
      "name": "Consulta general",
      "price_range": "S/ 50 - S/ 80",
      "duration": "30 minutos",
      "description": "Evaluación completa con radiografía panorámica"
    },
    {
      "name": "Ortodoncia",
      "price_range": "S/ 2,500 - S/ 5,000",
      "duration": "12-24 meses",
      "description": "Brackets metálicos o estéticos. Incluye controles mensuales."
    },
    {
      "name": "Extracción simple",
      "price_range": "S/ 100 - S/ 200",
      "duration": "30 minutos",
      "description": "Extracción de piezas dentales no complicadas"
    },
    {
      "name": "Endodoncia (tratamiento de conducto)",
      "price_range": "S/ 300 - S/ 600",
      "duration": "60-90 minutos",
      "description": "Tratamiento de conducto por pieza dental"
    },
    {
      "name": "Implante dental",
      "price_range": "S/ 2,000 - S/ 3,500",
      "duration": "3-6 meses (proceso completo)",
      "description": "Implante de titanio + corona. Requiere evaluación previa."
    },
    {
      "name": "Carillas dentales",
      "price_range": "S/ 400 - S/ 800 por pieza",
      "duration": "2-3 citas",
      "description": "Carillas de resina o porcelana"
    }
  ],
  "payment_methods": [
    "Efectivo",
    "Tarjeta de crédito/débito (Visa, Mastercard)",
    "Transferencia bancaria (BCP, Interbank, BBVA)",
    "Yape / Plin"
  ],
  "faq": [
    {
      "question": "¿Tienen estacionamiento?",
      "answer": "Sí, contamos con estacionamiento gratuito para pacientes."
    },
    {
      "question": "¿Atienden emergencias?",
      "answer": "Sí, atendemos emergencias dentales en horario de atención. Fuera de horario, comunícate al +51 905 858 566."
    },
    {
      "question": "¿Trabajan con seguros?",
      "answer": "Trabajamos con Rímac, Pacífico y Mapfre. Consulta cobertura específica con nuestro equipo."
    },
    {
      "question": "¿Primera cita tiene costo?",
      "answer": "La primera consulta de evaluación tiene un costo de S/ 50, que se descuenta si inicias tu tratamiento con nosotros."
    }
  ]
};

// Pasar datos al siguiente nodo
return [{
  json: {
    ...($json || {}),
    knowledge_base: KNOWLEDGE_BASE,
    clinic_name: KNOWLEDGE_BASE.clinic_info.name,
    knowledge_base_json: JSON.stringify(KNOWLEDGE_BASE, null, 2)
  }
}];

// Preparar prompt para LLM
const knowledge_base_json = $json.knowledge_base_json;
const message_text = $json.message_text;
const clinic_name = $json.clinic_name;

const system_prompt = `Eres SofIA, asistente virtual de la clínica dental ${clinic_name}.
Tu tarea es responder la pregunta del paciente usando ÚNICAMENTE la información proporcionada.

INFORMACIÓN DE LA CLÍNICA:
${knowledge_base_json}

REGLAS:
1. Responde SOLO con información que esté en la base de conocimiento
2. Si la información no está disponible, di EXACTAMENTE: "No tengo esa información disponible. Te conecto con un agente para ayudarte mejor."
3. Sé amable, conciso y profesional
4. No inventes precios, horarios ni servicios
5. Si el paciente pregunta algo que requiere evaluación médica, sugiere agendar una cita
6. Máximo 3 oraciones
7. Responde en español
8. Al final de tu respuesta, siempre pregunta: "¿Hay algo más en lo que pueda ayudarte?"`;

const user_prompt = `PREGUNTA DEL PACIENTE:
${message_text}`;

return [{
  json: {
    ...($json || {}),
    system_prompt: system_prompt,
    user_prompt: user_prompt
  }
}];

// Extraer respuesta del LLM
const response = $json;

// La respuesta de OpenAI viene en response.choices[0].message.content
const llm_response = response.choices?.[0]?.message?.content || '';

// Obtener datos originales del nodo "Preparar Prompt INFO"
const original_data = $node["Preparar Prompt INFO"].json;

return [{
  json: {
    ...original_data,  // Todos los datos originales (conversation_id, account_id, etc.)
    llm_response: llm_response.trim()
  }
}];

// ============================================
// VALIDACION ANTI-ALUCINACION
// ============================================

const llm_response = $json.llm_response || '';

// Flags de validación
let should_escalate = false;
let escalation_reason = '';

// Regla 1: Respuesta muy larga (>500 chars)
if (llm_response.length > 500) {
  should_escalate = true;
  escalation_reason = 'Respuesta LLM muy larga (>500 chars)';
}

// Regla 2: LLM dice que no tiene información
const no_info_keywords = [
  'no tengo esa información',
  'no dispongo',
  'no cuento con',
  'no tengo información',
  'no está disponible',
  'te conecto con un agente'
];

for (const keyword of no_info_keywords) {
  if (llm_response.toLowerCase().includes(keyword)) {
    should_escalate = true;
    escalation_reason = 'LLM indica falta de información';
    break;
  }
}

// Regla 3: Respuesta vacía o muy corta
if (llm_response.length < 10) {
  should_escalate = true;
  escalation_reason = 'Respuesta LLM vacía o muy corta';
}

return [{
  json: {
    ...($json || {}),
    should_escalate_info: should_escalate,
    escalation_reason_info: escalation_reason,
    validation_passed: !should_escalate
  }
}];

// ============================================
// CALCULATE AVAILABLE 30-MIN SLOTS
// ============================================
const busy_events = $input.all();

// Horarios de negocio
const business_hours = {
  1: { start: 9, end: 19 },  // Lunes
  2: { start: 9, end: 19 },  // Martes
  3: { start: 9, end: 19 },  // Miércoles
  4: { start: 9, end: 19 },  // Jueves
  5: { start: 9, end: 19 },  // Viernes
  6: { start: 9, end: 14 },  // Sábado
  0: null,                    // Domingo - cerrado
  7: null
};

// Extraer eventos ocupados
const busy_times = [];
for (const item of busy_events) {
  const event = item.json;
  if (event.start && event.end) {
    const start_time = event.start.dateTime || event.start.date;
    const end_time = event.end.dateTime || event.end.date;
    busy_times.push({
      start: new Date(start_time),
      end: new Date(end_time)
    });
  }
}

// Generar todos los slots posibles para los próximos 7 días
const slots = [];
const now = new Date();
const slot_duration = 30; // minutos

for (let day = 0; day < 7; day++) {
  const date = new Date(now);
  date.setDate(date.getDate() + day);
  date.setHours(0, 0, 0, 0);

  const day_of_week = date.getDay();
  const hours = business_hours[day_of_week];

  if (!hours) continue; // Día cerrado

  // Generar slots para este día
  for (let hour = hours.start; hour < hours.end; hour++) {
    for (let minute = 0; minute < 60; minute += slot_duration) {
      const slot_start = new Date(date);
      slot_start.setHours(hour, minute, 0, 0);

      const slot_end = new Date(slot_start);
      slot_end.setMinutes(slot_end.getMinutes() + slot_duration);

      // Skip slots in the past
      if (slot_start < now) continue;

      // Check if slot conflicts with busy times
      let is_available = true;
      for (const busy of busy_times) {
        if (slot_start < busy.end && slot_end > busy.start) {
          is_available = false;
          break;
        }
      }

      if (is_available) {
        slots.push({
          start: slot_start.toISOString(),
          end: slot_end.toISOString(),
          date: slot_start.toLocaleDateString('es-PE', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' }),
          time: slot_start.toLocaleTimeString('es-PE', { hour: '2-digit', minute: '2-digit' })
        });
      }
    }
  }
}

// Limitar a primeros 20 slots disponibles
const available_slots = slots.slice(0, 20);

return [{
  json: {
    ...$json,
    available_slots: available_slots,
    total_available: available_slots.length,
    busy_events_count: busy_times.length,
    calculated_at: new Date().toISOString()
  }
}];

// ============================================
// SELECT 3 BEST SLOTS TO OFFER
// ============================================
const all_slots = $json.available_slots || [];

if (all_slots.length === 0) {
  return [{
    json: {
      ...$json,
      selected_slots: [],
      no_slots_available: true,
      should_escalate: true,
      escalation_reason: 'NO_SLOTS_AVAILABLE',
      escalation_message: 'Lo siento, no hay horarios disponibles en los próximos 7 días. Te conecto con un agente.'
    }
  }];
}

// Tomar los primeros 3 slots disponibles
const selected = all_slots.slice(0, 3);

// Formatear para mensaje al paciente
const slot_options = selected.map((slot, idx) => {
  return {
    option_number: idx + 1,
    date: slot.date,
    time: slot.time,
    start_iso: slot.start,
    end_iso: slot.end
  };
});

return [{
  json: {
    ...$json,
    selected_slots: slot_options,
    has_slots: true,
    total_offered: slot_options.length
  }
}];

// ============================================
// FORMAT SLOT OFFER MESSAGE
// ============================================
const slots = $json.selected_slots || [];

if (slots.length === 0) {
  return [{
    json: {
      ...$json,
      offer_message: 'Lo siento, no hay horarios disponibles en los próximos 7 días.',
      should_escalate: true,
      escalation_reason: 'NO_SLOTS_AVAILABLE'
    }
  }];
}

// Formatear mensaje con las 3 opciones
let message = '¡Perfecto! Tengo estos horarios disponibles para tu cita:\n\n';

slots.forEach(slot => {
  message += `${slot.option_number}. ${slot.date} a las ${slot.time}\n`;
});

message += '\nPor favor responde con el número de la opción que prefieres (1, 2 o 3).';

return [{
  json: {
    ...$json,
    offer_message: message,
    offered_slots: slots,
    awaiting_slot_confirmation: true,
    should_escalate: false
  }
}];

// ============================================
// PREPARE ESCALATION WITH SLOT CONTEXT
// ============================================
const slots = $json.offered_slots || [];

let escalation_note = 'Paciente solicitó agendar cita.\n\n';
escalation_note += 'Horarios ofrecidos:\n';

slots.forEach(slot => {
  escalation_note += `${slot.option_number}. ${slot.date} a las ${slot.time}\n`;
});

escalation_note += '\nEsperando respuesta del paciente para confirmar el horario.';

return [{
  json: {
    ...$json,
    escalation_message: 'Gracias. Un agente se pondrá en contacto contigo pronto para confirmar tu cita.',
    escalation_note: escalation_note,
    escalation_reason: 'PHASE3_SLOTS_OFFERED',
    should_escalate: true
  }
}];
