// ============================================
// VALIDAR INPUT + DETECTAR CONTACTO NUEVO
// ============================================
const payload = $input.item.json.body || $input.item.json;

// Campos del evento
const event = payload.event;
const content = payload.content;
const conversation_id = payload.conversation?.id;
const inbox_id = payload.conversation?.inbox_id;
const clinic_id = payload.conversation?.custom_attributes?.clinic_id;
const patient_id = payload.conversation?.custom_attributes?.patient_id;
const contact_phone = payload.conversation?.contact_inbox?.source_id;
const contact_id = payload.sender?.id;
const channel_type = payload.conversation?.contact_inbox?.inbox?.channel_type;
const account_id = payload.account?.id || 2;
const message_type = payload.message_type;
const created_at = payload.created_at;

// ---- Validar evento ----
if (!event || !content) {
  throw new Error(`Missing event or content`);
}

// ---- Ignorar eventos que no son mensajes ----
if (event !== 'message_created') {
  throw new Error(`Ignored event: ${event}`);
}

// ---- Ignorar mensajes de tipo outgoing (evitar loops) ----
if (message_type !== 'incoming') {
  throw new Error(`Ignored: not incoming message (type: ${message_type})`);
}

// ---- Detectar si es contacto nuevo sin contact_inbox ----
const contact_inboxes = payload.conversation?.contact_inbox;
const has_contact_inbox = !!contact_inboxes;

// Obtener contador de interacciones bot
const bot_count = payload.conversation?.custom_attributes?.bot_interaction_count || 0;
const last_bot_message = payload.conversation?.custom_attributes?.last_bot_message_at;

return [{
  json: {
    message_text: (content || '').trim(),
    conversation_id: conversation_id,
    inbox_id: inbox_id,
    inbox_id_api: 2,
    inbox_id_db: 3,
    clinic_id: clinic_id || 'default',
    patient_id: patient_id,
    contact_phone: contact_phone,
    contact_id: contact_id,
    account_id: account_id,
    message_type: message_type,
    message_timestamp: created_at,
    bot_interaction_count: bot_count,
    last_bot_message_at: last_bot_message,
    sender_name: payload.sender?.name || 'Paciente',
    sender_email: payload.sender?.email,
    conversation_status: payload.conversation?.status,
    has_contact_inbox: has_contact_inbox,
    channel_type: channel_type || 'Channel::WebWidget',
    raw_payload: payload
  }
}];

// ============================================
// WHATSAPP SAFE RULES - FASE 1
// ============================================
const bot_count = $json.bot_interaction_count || 0;
const message_lower = ($json.message_text || '').toLowerCase();

// Regla 1: MÃ¡ximo 1 interacciÃ³n automÃ¡tica en Fase 1
if (bot_count >= 1) {
  return [{
    json: {
      ...$json,
      should_escalate: true,
      escalation_reason: 'MAX_INTERACTIONS_PHASE1',
      escalation_message: 'Te conecto con un agente de inmediato.'
    }
  }];
}

// Regla 2: Mensaje > 24h
const message_age_hours = (Date.now() - ($json.message_timestamp * 1000)) / (1000 * 60 * 60);
if (message_age_hours > 24) {
  return [{
    json: {
      ...$json,
      should_escalate: true,
      escalation_reason: 'MESSAGE_TOO_OLD',
      escalation_message: 'Te conecto con un agente.'
    }
  }];
}

// Regla 3: Horario comercial (Lima, Peru)
const now = new Date();
const lima_hour = parseInt(now.toLocaleString('en-US', { 
  timeZone: 'America/Lima', 
  hour: 'numeric', 
  hour12: false 
}));

if (lima_hour < 8 || lima_hour >= 22) {
  return [{
    json: {
      ...$json,
      should_escalate: true,
      escalation_reason: 'OUTSIDE_BUSINESS_HOURS',
      escalation_message: 'Gracias por escribirnos. Te responderemos en horario de atenciÃ³n (8am - 10pm).'
    }
  }];
}

// Regla 4: Emergencias
const emergency_keywords = [
  'emergencia', 'urgencia', 'dolor fuerte', 'sangra', 'mucho dolor',
  'me caÃ­', 'golpe', 'accidente', 'no puedo', 'ayuda urgente',
  'hinchazÃ³n', 'infecciÃ³n', 'fiebre'
];
const is_emergency = emergency_keywords.some(kw => message_lower.includes(kw));

if (is_emergency) {
  return [{
    json: {
      ...$json,
      should_escalate: true,
      escalation_reason: 'EMERGENCY_DETECTED',
      escalation_message: 'Detecto que podrÃ­as tener una urgencia. Te conecto de inmediato con nuestro equipo.',
      priority: 'urgent'
    }
  }];
}

// Regla 5: Opt-out
const opt_out_keywords = ['detente', 'ya no', 'basta', 'stop', 'cancelar bot', 'agente', 'humano', 'persona'];
const wants_opt_out = opt_out_keywords.some(kw => message_lower.includes(kw));

if (wants_opt_out) {
  return [{
    json: {
      ...$json,
      should_escalate: true,
      escalation_reason: 'USER_OPT_OUT',
      escalation_message: 'Entendido. Te conecto con un agente humano.'
    }
  }];
}

// Todo OK - continuar al clasificador
return [{
  json: {
    ...$json,
    should_escalate: false,
    whatsapp_safe: true,
    checked_at: new Date().toISOString()
  }
}];

// ============================================
// NORMALIZAR OUTPUT DEL CLASIFICADOR
// ============================================
let intent_data = $json.output || $json;

if (typeof intent_data === 'string') {
  try {
    intent_data = JSON.parse(intent_data);
  } catch (e) {
    const text = intent_data.toLowerCase();
    if (text.includes('create_event')) {
      intent_data = { intent: 'CREATE_EVENT', confidence: 'low' };
    } else if (text.includes('info')) {
      intent_data = { intent: 'INFO', confidence: 'low' };
    } else if (text.includes('payment')) {
      intent_data = { intent: 'PAYMENT', confidence: 'low' };
    } else {
      intent_data = { intent: 'HUMAN', confidence: 'low' };
    }
  }
}

let intent = (intent_data.intent || 'HUMAN').trim().toUpperCase();
let confidence = (intent_data.confidence || 'low').toLowerCase();

const valid_intents = ['CREATE_EVENT', 'INFO', 'PAYMENT', 'HUMAN'];
if (!valid_intents.includes(intent)) {
  intent = 'HUMAN';
  confidence = 'low';
}

return [{
  json: {
    ...$json,
    intent: intent,
    confidence: confidence,
    classified_at: new Date().toISOString(),
    phase: 'PHASE_1_TESTING',
    action: 'ESCALATE_TO_HUMAN'
  }
}];

// ============================================
// CREAR CONTACTO + CONVERSACIÃ“N EN CHATWOOT
// ============================================
const CHATWOOT_URL = $env.CHATWOOT_URL || 'https://chat.redsolucionesti.com';
const API_KEY = $env.CHATWOOT_API_KEY || 'yypAwZDH2dV3crfbqJqWCgj1';
const ACCOUNT_ID = 2;
const INBOX_ID_API = 2;

const contact_name = $json.name || $json.new_contact_name || 'Paciente Nuevo';
const contact_email = $json.email || $json.new_contact_email || null;
const contact_phone = $json.phone || $json.new_contact_phone || null;

let contact_id = null;
let search_query = contact_email || contact_phone || contact_name;

try {
  const searchResponse = await fetch(
    `${CHATWOOT_URL}/api/v1/accounts/${ACCOUNT_ID}/contacts/search?q=${encodeURIComponent(search_query)}&page=1`,
    { method: 'GET', headers: { 'api_access_token': API_KEY } }
  );
  const searchData = await searchResponse.json();

  if (searchData.payload && searchData.payload.length > 0) {
    const existing = searchData.payload[0];
    contact_id = existing.id;
    if (existing.contact_inboxes && existing.contact_inboxes.length > 0) {
      return [{
        json: {
          contact_id: contact_id,
          contact_status: 'EXISTING_WITH_INBOX',
          contact_inbox_exists: true,
          message: 'Contacto ya existe y tiene inbox asignado'
        }
      }];
    }
  }
} catch (e) {}

if (!contact_id) {
  try {
    const createBody = { name: contact_name };
    if (contact_email) createBody.email = contact_email;
    if (contact_phone) createBody.phone_number = contact_phone;

    const createResponse = await fetch(
      `${CHATWOOT_URL}/api/v1/accounts/${ACCOUNT_ID}/contacts`,
      {
        method: 'POST',
        headers: { 'api_access_token': API_KEY, 'Content-Type': 'application/json' },
        body: JSON.stringify(createBody)
      }
    );
    const createData = await createResponse.json();

    if (createData.payload?.contact?.id) {
      contact_id = createData.payload.contact.id;
    } else if (createData.id) {
      contact_id = createData.id;
    } else {
      throw new Error('No contact_id in response: ' + JSON.stringify(createData));
    }
  } catch (e) {
    return [{ json: { contact_status: 'CREATION_FAILED', error: e.message } }];
  }
}

try {
  const convResponse = await fetch(
    `${CHATWOOT_URL}/api/v1/accounts/${ACCOUNT_ID}/conversations`,
    {
      method: 'POST',
      headers: { 'api_access_token': API_KEY, 'Content-Type': 'application/json' },
      body: JSON.stringify({
        inbox_id: INBOX_ID_API,
        contact_id: contact_id,
        message: { content: `Â¡Hola ${contact_name}! Bienvenido/a a nuestra clÃ­nica. Soy SofIA, tu asistente virtual. Â¿En quÃ© puedo ayudarte?` }
      })
    }
  );

  const convData = await convResponse.json();

  if (convData.error) {
    const retryResponse = await fetch(
      `${CHATWOOT_URL}/api/v1/accounts/${ACCOUNT_ID}/conversations`,
      {
        method: 'POST',
        headers: { 'api_access_token': API_KEY, 'Content-Type': 'application/json' },
        body: JSON.stringify({
          inbox_id: 3,
          contact_id: contact_id,
          message: { content: `Â¡Hola ${contact_name}! Bienvenido/a a nuestra clÃ­nica. Soy SofIA, tu asistente virtual. Â¿En quÃ© puedo ayudarte?` }
        })
      }
    );
    const retryData = await retryResponse.json();

    if (retryData.error) {
      return [{
        json: {
          contact_id: contact_id,
          contact_status: 'CONTACT_CREATED_BUT_CONVERSATION_FAILED',
          error: retryData.error,
          note: 'IntentÃ³ con inbox_id 2 y 3, ambos fallaron'
        }
      }];
    }

    return [{
      json: {
        contact_id: contact_id,
        conversation_id: retryData.id,
        contact_status: 'CREATED_OK',
        contact_inbox_exists: true,
        inbox_id_used: 3,
        message: 'Contacto y conversaciÃ³n creados (inbox_id=3)'
      }
    }];
  }

  return [{
    json: {
      contact_id: contact_id,
      conversation_id: convData.id,
      contact_status: 'CREATED_OK',
      contact_inbox_exists: true,
      inbox_id_used: INBOX_ID_API,
      message: 'Contacto y conversaciÃ³n creados correctamente'
    }
  }];

} catch (e) {
  return [{ json: { contact_id: contact_id, contact_status: 'CONVERSATION_CREATION_ERROR', error: e.message } }];
}
